import './App.css';
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import Arara from "./animais/Arara";
import Baleia from "./animais/Baleia";
import Onca from "./animais/Onca";
import Pinguim from "./animais/Pinguim";
import Nav_bar from './nav_bar/'
import Info from './info/'
import arara from "../image/Arara_Azul.jpg"
import pinguim from "../image/pinguim.jpg"
import onca from "../image/Onca.JPG"
import baleia from "../image/baleiaazul.jpg"

export default function App() {
  const animais = [
    {
      nome: "Arara Azul",
      descricao:
        "A arara-azul é uma das aves mais icônicas da fauna brasileira e é considerada uma espécie bandeira de conservação.",
      imagem: { arara },
      link: "/arara"
    },
    {
      nome: "Pinguim Africano",
      descricao:
        "O pinguim-africano é uma espécie de pinguim que ocorre na África do Sul e nas ilhas próximas.",
      imagem: "https://via.placeholder.com/150",
      link: "/pinguim"
    },
    {
      nome: "Onça Pintada",
      descricao:
        "A onça-pintada é uma das espécies mais emblemáticas da fauna brasileira e é considerada um dos principais ícones da biodiversidade nacional.",
      imagem: "https://via.placeholder.com/150",
      link: "/onca"
    },
    {
      nome: "Baleia Fin",
      descricao:
        "A baleia-fin é um animal migratório que tem uma ampla distribuição geográfica, ocorrendo em todos os oceanos.",
      imagem: "https://via.placeholder.com/150",
      link: "/baleia"
    }
  ];

  return (
    <div className="App">
      <Nav_bar />
      <Info />
      <Router>
        <div>
          <nav>
            <ul>
              <li>
                <Link to="/">Animais em extinção</Link>
              </li>
              {animais.map((animal, index) => (
                <li key={index}>
                  <Link to={animal.link}>{animal.nome}</Link>
                </li>
              ))}
            </ul>
          </nav>

          <Switch>
            <Route path="/" exact>
              <div className="AnimaisDash">
                {animais.map((animal, index) => (
                  <div className="AnimaisDash-item" key={index}>
                    <img src={animal.imagem} alt={animal.nome} />
                    <h3>{animal.nome}</h3>
                    <p>{animal.descricao}</p>
                  </div>
                ))}
              </div>
            </Route>
            <Route path="/arara" component={Arara} />
            <Route path="/pinguim" component={Pinguim} />
            <Route path="/onca" component={Onca} />
            <Route path="/baleia" component={Baleia} />
          </Switch>
        </div>
      </Router>
    </div>
  );
}
